INSERT INTO bracelets (name, image, description, details, price) VALUES
('Arctic Gleam', 'https://i.ibb.co/S5qrX4g/arctic-gleam.jpg', 'The Arctic Gleam tennis bracelet is a celebration of refined elegance. Featuring a continuous row of radiant round-cut diamonds set in polished platinum, it’s the perfect piece for elevating any outfit with effortless sophistication.', ARRAY['Platinum setting', 'Round-cut diamonds', 'Length: 7.5 inches', 'Designed for everyday luxury', 'Product number: 70183001'], '$10,200'),
